import React, { useEffect, useState } from "react";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";

const MyOrder = () => {
  const [orderData, setOrderData] = useState([]);

  const fetchMyOrder = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/MyOrderData", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: localStorage.getItem("userEmail"),
        }),
      });

      if (!response.ok) {
        throw new Error("Failed to fetch data");
      }

      const data = await response.json();
      console.log("Data from API:", data);

      if (data && data.orderData && Array.isArray(data.orderData.order_data)) {
        setOrderData(data.orderData.order_data.reverse());
      } else {
        console.error("Invalid data structure from API");
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchMyOrder();
  }, []);

  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="row">
          {orderData.length > 0 ? (
            orderData.map((item, index) => (
              <div key={`orderItem-${index}`}>
                {item.map((arrayData, subIndex) => (
                  <div key={`arrayItem-${subIndex}`}>
                    {arrayData.Order_date ? (
                      <div className="m-auto mt-5">
                        {arrayData.Order_date}
                        <hr />
                      </div>
                    ) : (
                      <div
                        className="col-12 col-md-6 col-lg-3"
                        key={`cardItem-${subIndex}`}
                      >
                        <div
                          className="card mt-3"
                          style={{ width: "16rem", maxHeight: "360px" }}
                        >
                          <img
                            src={arrayData.img}
                            className="card-img-top"
                            alt="..."
                            style={{
                              height: "120px",
                              objectFit: "fill",
                            }}
                          />
                          <div className="card-body">
                            <h5 className="card-title">{arrayData.name}</h5>
                            <div
                              className="container w-100 p-0"
                              style={{ height: "38px" }}
                            >
                              <span className="m-1">{arrayData.qty}</span>
                              <span className="m-1">{arrayData.size}</span>
                              <span className="m-1">
                                {arrayData.Order_date}
                              </span>
                              <div className="d-inline ms-2 h-100 w-20 fs-5">
                                ₹{arrayData.price}/-
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            ))
          ) : (
            <p>No orders found.</p>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default MyOrder;
