import React from "react";

import { useDispatch, useSelector } from "react-redux";
import { add } from "../store/cartSlice";

const Card = (props) => {
  const { options, foodItem } = props;

  const dispatch = useDispatch();

  const priceOptions = Object.keys(options);

  const handleAdd = (product) => {
    dispatch(add(product));
  };

  return (
    <div>
      <div className="card mt-3" style={{ width: "18rem" }}>
        <img
          src={foodItem.imgSrc}
          className="card-img-top w-100"
          alt={foodItem.name}
          style={{ objectFit: "fill", height: "220px" }}
        />
        <div className="card-body">
          <h5 className="card-title">{foodItem.name}</h5>
          <div className="container w-100 d-flex align-items-center">
            <select className="m-2 h-100 bg-success text-white">
              {Array.from(Array(6), (x, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>

            <select className="m-2 h-100 bg-success text-white rounded">
              {priceOptions.map((data) => (
                <option value={data} key={data}>
                  {data}
                </option>
              ))}
            </select>

            <div className="d d-inline h-100 fs-5">Total Price</div>
          </div>
          <hr></hr>
          <button
            className="btn btn-success justify-center ms-2"
            onClick={() => handleAdd(foodItem)}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default Card;
