import React, { useEffect, useState } from "react";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";

export default function MyOrder() {
  const [orderData, setOrderData] = useState([]);

  useEffect(() => {
    // Fetch data from the API
    fetch("http://localhost:5000/api/MyOrderData")
      .then((response) => response.json())
      .then((data) => setOrderData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const formatDate = (dateStr) => {
    const options = {
      weekday: "Thu",
      year: "numeric",
      month: "short",
      day: "numeric",
    };
    return new Date(dateStr).toLocaleDateString("en-US", options);
  };

  return (
    <div>
      {orderData.map((order) => (
        <div key={order._id}>
          <h3>Email: {order.email}</h3>
          {order.order_data.map((item) => (
            <div key={item.id}>
              {Array.isArray(item) ? (
                item.map((subItem) => (
                  <div key={subItem.id}>
                    <p>Order Date: {formatDate(subItem.Order_date)}</p>
                    <p>Name: {subItem.name}</p>
                    <p>Price: {subItem.price}</p>
                    <p>Quantity: {subItem.qty}</p>
                    <p>Size: {subItem.size}</p>
                    <img
                      src={subItem.img}
                      alt={subItem.name}
                      style={{ width: "100px", height: "100px" }}
                    />
                  </div>
                ))
              ) : (
                <>
                  <p>Order Date: {formatDate(item.Order_date)}</p>
                  <p>Name: {item.name}</p>
                  <p>Price: {item.price}</p>
                  <p>Quantity: {item.qty}</p>
                  <p>Size: {item.size}</p>
                  <img
                    src={item.img}
                    alt={item.name}
                    style={{ width: "100px", height: "100px" }}
                  />
                </>
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}
