import React, { useEffect, useRef, useState } from "react";
import { useDispatch } from "react-redux";

import { add } from "../store/CartSlice";
const Card = (props) => {
  const dispatch = useDispatch();

  const priceRef = useRef();

  const sizeRef = useRef();

  const { foodName, imgSrc, options, foodItem } = props;

  const priceOptions = Object.keys(options);

  const [size, setSize] = useState(Object.keys(options)[0] || ""); // Set initial size

  const [qty, setQty] = useState(1);

  const selectedOption = options[size]; // Get the price based on the selected size
  const price = selectedOption * qty;
  const handleAddtoCart = async (foodItem) => {
    await dispatch(
      add({
        id: foodItem._id,
        img: imgSrc,
        name: foodName,
        price: price,
        qty: qty,
        size: size,
      })
    );
  };

  useEffect(() => {
    setSize(sizeRef.current.value);
  }, []);

  return (
    <div>
      <div className="card mt-3" style={{ width: "18rem" }}>
        <img
          src={imgSrc}
          className="card-img-top w-100"
          alt={foodName}
          style={{ objectFit: "fill", height: "220px" }}
        />
        <div className="card-body">
          <h5 className="card-title">{foodName}</h5>
          <div className="container w-100 d-flex align-items-center">
            <select
              className="m-2 h-100 bg-success text-white"
              onChange={(e) => setSize(e.target.value)}
              ref={sizeRef}
            >
              {Array.from(Array(6), (x, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>

            <select
              className="m-2 h-100 bg-success text-white rounded"
              onChange={(e) => setQty(e.target.value)}
              ref={priceRef}
            >
              {priceOptions.map((data) => (
                <option value={data} key={data}>
                  {data}
                </option>
              ))}
            </select>

            <div className="d d-inline h-100 fs-5">
              <h5 className="my-1">â‚¹{price}/-</h5>
            </div>
          </div>
          <hr></hr>
          <button
            className="btn btn-success justify-center ms-2"
            onClick={() => handleAddtoCart(foodItem)}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default Card;
