import React, { useEffect, useState } from "react";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";

const MyOrder = () => {
  const [orderData, setOrderData] = useState({});

  const fetchMyOrder = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/MyOrderData", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: localStorage.getItem("userEmail"),
        }),
      });

      if (!response.ok) {
        throw new Error("Failed to fetch data");
      }

      const data = await response.json();
      setOrderData(data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchMyOrder();
  }, []);

  return (
    <div>
      <Navbar />

      <div className="container">
        <div className="row">
          {orderData && orderData.orderData
            ? orderData.orderData.order_data.map((orderGroup) => {
                const orderDate = orderGroup[1]; // Assuming the date is always at index 1
                const items = orderGroup[0];

                return (
                  <div key={orderDate} className="m-auto mt-5">
                    <h5>{orderDate}</h5>
                    <hr />

                    <div className="row">
                      {items.map((item) => (
                        <div key={item.id} className="col-12 col-md-6 col-lg-3">
                          <div
                            className="card mt-3"
                            style={{ width: "16rem", maxHeight: "360px" }}
                          >
                            <img
                              src={item.img}
                              className="card-img-top"
                              alt={item.name}
                              style={{ height: "120px", objectFit: "fill" }}
                            />
                            <div className="card-body">
                              <h5 className="card-title">{item.name}</h5>
                              <div
                                className="container w-100 p-0"
                                style={{ height: "38px" }}
                              >
                                <span className="m-1">{item.qty}</span>
                                <span className="m-1">{item.size}</span>
                                <span className="m-1">{orderDate}</span>
                                <div className="d-inline ms-2 h-100 w-20 fs-5">
                                  ₹{item.price}/-
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                );
              })
            : ""}
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default MyOrder;
