import React, { useEffect, useState } from "react";

const MyOrder = () => {
  const [orderData, setOrderData] = useState([]);
  const userEmail = localStorage.getItem("userEmail");

  const fetchMyOrder = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/MyOrderData", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: userEmail,
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setOrderData(data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchMyOrder();
  }, []);
  const formatDate = (dateStr) => {
    const options = {
      weekday: "Thu",
      year: "numeric",
      month: "short",
      day: "numeric",
    };
    return new Date(dateStr).toLocaleDateString("en-US", options);
  };

  const groupOrdersByDate = (orders) => {
    const groupedOrders = {};

    orders.forEach((order) => {
      const orderDate = formatDate(order.order_data[0].Order_date);
      if (!groupedOrders[orderDate]) {
        groupedOrders[orderDate] = [];
      }

      groupedOrders[orderDate].push(order);
    });

    return groupedOrders;
  };

  const groupedOrders = groupOrdersByDate(orderData);

  return (
    <div>
      {Object.keys(groupedOrders).map((date) => (
        <div key={date}>
          <h2>{date}</h2>
          {groupedOrders[date].map((order) => (
            <div key={order._id}>
              <h3>Email: {order.email}</h3>
              {order.order_data.map((item) => (
                <div key={item.id}>
                  {Array.isArray(item) ? (
                    item.map((subItem) => (
                      <div key={subItem.id}>
                        <p>Name: {subItem.name}</p>
                        <p>Price: {subItem.price}</p>
                        <p>Quantity: {subItem.qty}</p>
                        <p>Size: {subItem.size}</p>
                        <img
                          src={subItem.img}
                          alt={subItem.name}
                          style={{ width: "100px", height: "100px" }}
                        />
                      </div>
                    ))
                  ) : (
                    <>
                      <p>Name: {item.name}</p>
                      <p>Price: {item.price}</p>
                      <p>Quantity: {item.qty}</p>
                      <p>Size: {item.size}</p>
                      <img
                        src={item.img}
                        alt={item.name}
                        style={{ width: "100px", height: "100px" }}
                      />
                    </>
                  )}
                </div>
              ))}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default MyOrder;
